class Solution():

    def minimumTime(self, grid: List[List[int]]) -> int:
        '\n    You are given a m x n matrix grid consisting of non-negative integers where grid[row][col] represents the minimum time required to be able to visit the cell (row, col), which means you can visit the cell (row, col) only when the time you visit it is greater than or equal to grid[row][col].\n    You are standing in the top-left cell of the matrix in the 0th second, and you must move to any adjacent cell in the four directions: up, down, left, and right. Each move you make takes 1 second.\n    Return the minimum time required in which you can visit the bottom-right cell of the matrix. If you cannot visit the bottom-right cell, then return -1.\n    '
        from heapq import heappush, heappop
        from typing import List
        (m, n) = (len(grid), len(grid[0]))
        visited = [[False for _ in range(n)] for _ in range(m)]
        directions = [(0, 1), (1, 0), (0, (- 1)), ((- 1), 0)]
        queue = [(grid[0][0], 0, 0)]
        while queue:
            (time, x, y) = heappop(queue)
            if ((x == (m - 1)) and (y == (n - 1))):
                return time
            for (dx, dy) in directions:
                (nx, ny) = ((x + dx), (y + dy))
                if ((0 <= nx < m) and (0 <= ny < n) and (not visited[nx][ny])):
                    visited[nx][ny] = True
                    heappush(queue, (max(time, grid[nx][ny]), nx, ny))
        return (- 1)
